// runtime, space: O(nlogn)
fun rangeSum(nums: IntArray, n: Int, left: Int, right: Int): Int {
    if(n == 1) {
        return nums[0]
    }

    // Pair(accumulated, currIdx)
    val pq = PriorityQueue<Pair<Int, Int>>() { o1, o2 ->
        o1.first - o2.first
    }

    for(i in 0 until n) {
        pq.offer(Pair(nums[i], i))
    }

    var remainingToSkip = left - 1
    var remainingToSum = right - left + 1

    while(remainingToSkip > 0) {
        pqPollAndUpdate(pq, nums, n)
        remainingToSkip--
    }

    var sum = 0L
    while(remainingToSum > 0) {
        sum += pqPollAndUpdate(pq, nums, n)
        remainingToSum--
    }


    val mod = 1_000_000_007
    return (sum%mod).toInt()
}

fun pqPollAndUpdate(pq: PriorityQueue<Pair<Int, Int>>, nums: IntArray, n: Int): Int {
    val curr = pq.poll();

    if(curr.second < (n - 1) ) {
        val next = curr.second + 1
        pq.offer(Pair(curr.first + nums[next], next))
    }

    return curr.first
}




//beating 100%; don't think it's optimized
//runtime: n^2, space: n^2
fun rangeSumBF(nums: IntArray, n: Int, left: Int, right: Int): Int {
    if(n == 1) {
        return nums[0]
    }

    val unsorted = ArrayList<Int>(n * (n + 1 ) / 2)
    val runningSumArray = runningSum(nums)
    unsorted.addAll(runningSumArray.toList())
    for(i in 1 until runningSumArray.size) {
        for(j in i until runningSumArray.size) {
            unsorted.add(nums[j] - nums[i-1])
        }
    }

    unsorted.sort()

    var sum = 0L
    for(i in (left - 1) until right) {
        sum += unsorted[i]
    }
    val mod = 10.0.pow(9.0) + 7
    return (sum%mod).toInt()
}

fun runningSum(nums: IntArray): IntArray {
    if(nums.size <= 1) {
        return nums;
    }

    for(i in 1 until nums.size) {
        nums[i] = nums[i] + nums[i-1]
    }
    return nums;
}

